targets.expr:
sum by (common_name) (
  label_replace(
    rate(container_network_transmit_bytes_total{pod=~"(payments-service|orders-service).*"}[$__rate_interval]),
    "common_name", "$1", "pod", "(payments-service|orders-service).*"
  )
)
/ on(common_name)
sum by (common_name) (
  label_replace(
    rate(http_server_requests_seconds_count{uri!~"/actuator.*"}[$__rate_interval]),
    "common_name", "$1", "service", "(.*)"
  )
)
